import React from "react";
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
// import room1 from './images/room-1.jpg'
import room2 from './images/room1.png'
import room3 from './images/room2.png'
import room4 from './images/room8.jpg'


const data=[room2,room3,room4]

export default function RoomDescription(props){
    const[image,setImage]=useState(0)
    useEffect(()=>{
        setInterval(()=>{
            setImage((previmg)=>{
                if(previmg===4){
                    return 0
                }else{
                    setImage(previmg+1)
                }
            })
        },2000)
    },[])
    const params=useParams();
    console.log(params);
    
    return(
        <>
        <div>{props.description}</div>
        <img style={{width:600}} src={data[image]} alt="room" />
        </>
    )
}
