import React from "react";
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import room1 from './images/room-1.jpg'
import room2 from './images/room7.jpg'
import room3 from './images/room4.jpg'
import room4 from './images/room8.jpg'
import './components/Apartament.css'



export default function RoomDescription(props){
    const data=[room1,room2,room3,room4];
    const[image,setImage]=useState(0)
    // const params=useParams();
    // console.log(params);
    const handleLeftClick=()=>{
        if(image===0){
            setImage(3)
        }else{
            setImage(image-1)
        }
    }
    const handleRightClick=()=>{
        if(image===3){
            setImage(0)
        }else{
            setImage(image+1)
        }
    }
    const handleDot=(index)=>{
        setImage(index)
    }
    const onImageClick=(i)=>{
        setImage(i)
    }
    return(
        <>
        <div className="sliderimg">
             <i onClick={handleLeftClick} class="fa-solid fa-chevron-left arrowprevious"></i>
             <img style={{width:600,height:400,objectFit:'contain'}} src={data[image]} alt="room" />
             <i onClick={handleRightClick} class="fa-solid fa-chevron-right arrownext"></i>
        
        <div className="dots-div">
          {data.map((element,index)=>{
          return(
              <div key={index}>
                    <i onClick={()=>handleDot(index)} class="fa-solid fa-circle dot"></i>
              </div>
            )
          })}
          </div>
      </div>
      <div>
        {data.map((element,i)=>{
            return(
                <div onClick={()=>onImageClick(i)} key={i}>
                    <img src={element} alt="img" />
                </div>
            )
        })}
      </div>
        </>
    )
}
