import React from "react";
import Header from "../Header";
import './Apartament.css'
import CheckInSection from '../CheckInSection'
import HotelRoom from "./HotelRoom";
import room1 from '../images/room-1.jpg'
import room2 from '../images/room2.jpg'
import room3 from '../images/room7.jpg'
import room4 from '../images/room14.jpg'
import { useState } from "react";
import { Link, useParams } from "react-router-dom";

export default function Apartament(){
    const[state,setState]=useState([
        {
             suite:'Junior Suite',
             size:'260',
             bed:'2',
             price:'350',
             src:room1,
             id:0
        },
        {
            suite:'Standart Room',
            size:'230',
            bed:'1',
            price:'250',
            src:room2,
            id:1
        },
        {
            suite:'Superior Room',
            size:'270',
            bed:'1',
            price:'550',
            src:room3,
            id:2
        },
        {
            suite:'Standart Room',
            size:'280',
            bed:'2',
            price:'250',
            src:room4,
            id:3
        }
    ])
    const params=useParams();
    console.log(params);

    return(
        <>
        <div className="book-background">
        {/* <Header background2={`background2`} background={`back2`} backApart={`backapart`} heading='Our Apartament'/> */}
        <CheckInSection
         HotelRoom={state.map((element)=>{
            return(
                <Link to={`/Our Apartament/${element.id}`} key={element.id}><HotelRoom suite={element.suite} size={element.size} bed={element.bed} price={element.price} src={element.src}/></Link>
            )
         })}
         className={`formmaindiv-apartm`}
          /> 
         </div>
        </>
    )
}
