import { Outlet, useLocation } from "react-router-dom"
import Header from "./Header"


function Root(){
    const location=useLocation();
    console.log(location);
    return(
        <div className={location.pathname==='/'?'background':location.pathname==='/Our%20Apartament'?'background2':location.pathname==='/Rio'?'backgroundrio':location.pathname==='/Contact'?'backgroundcontact':''}>
        <Header/>
        <Outlet/>
        </div>
    )
}
export default Root
