import { useState } from "react"
import '../src/RoomDescription.css'
function Slider(props) {
    const[image,setImage]=useState(0,{border:'none'})
    const handleLeftClick=()=>{
        if(image===0){
            setImage(props.data.length-1)
        }else{
            setImage(image-1)
        }
    }
    const handleRightClick=()=>{
        if(image===props.data.length-1){
            setImage(0)
        }else{
            setImage(image+1)
        }
    }
    const onImageClick=(i)=>{
        setImage(i)
        setImage({border:'1px solid red'} )
    }
    return(
        <div>
          <div className="sliderimg slide" >
               <i onClick={handleLeftClick} class="fa-solid fa-chevron-left arrowprevious"></i>
               <img className="slider-image" src={props.data[image]} alt="room" />
               <i onClick={handleRightClick} class="fa-solid fa-chevron-right arrownext"></i>
        </div>
        <div className="dataimgdiv">
          {props.data.map((element,i)=>{
              return(
                  <div onClick={()=>onImageClick(i)} key={i} className='dataimg'>
                      <img className="slider-small-images" src={element} alt="img" />
                  </div>
              )
          })}
        </div>
    </div>
)
}
export default Slider
