      }
    return(
        <div>
        <div>
            <div className="search-wraper">
                {searchInput}
            <input
                className="search-input"
                type="text"
                placeholder="Search here"
                onChange={handleChange}
                value={searchInput} 
                />
                <i onClick={handleClick} class="fa-solid fa-magnifying-glass search-icon"></i>
            </div>
        </div>
        </div>
        )
}
