import React from "react";
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import room1 from './images/room-1.jpg'
import room2 from './images/room7.jpg'
import room3 from './images/room4.jpg'
import room4 from './images/room8.jpg'


const data=[room1,room2,room3,room4]

export default function RoomDescription(props){
    const[image,setImage]=useState(0)
    const params=useParams();
    console.log(params);
    const handleLeftClick=()=>{
        if(image===0){
            setImage(4)
        }else{
            setImage(image-1)
        }
        

        
    }
    const handleRightClick=()=>{
        setImage(image+1)

    }
    return(
        <>
        <div>{props.description}</div>
        <div onClick={handleLeftClick}>
        <i style={{fontSize:35}}  class="fa-solid fa-arrow-left"></i>
        </div>
        <img style={{width:600}} src={data[image]} alt="room" />
        <i onClick={handleRightClick} style={{fontSize:35}} class="fa-solid fa-arrow-right"></i>
        </>
    )
}
