import { useState } from "react"
import '../src/RoomDescription.css'
import Slider from 'react-animated-slider';
// import ImageGallery from 'react-image-gallery';
// import "react-image-gallery/styles/css/image-gallery.css";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from 'react-responsive-carousel';

import room9 from './images/h.jpg'
import room10 from './images/room14.jpg'
import room11 from './images/bathroom-01.jpg'
import room12 from './images/towel.jpeg'
import room13 from './images/towel3.jpg'




function SlideShow(props) {

    const slide=[room9,room10,room11,room12,room13]

    const[image,setImage]=useState(0)
    const handleLeftClick=()=>{
        if(image===0){
            setImage(props.data.length-1)
        }else{
            setImage(image-1)
        }
    }
    const handleRightClick=()=>{
        if(image===props.data.length-1){
            setImage(0)
            
        }else{
            setImage(image+1)
        }
    }
    const onImageClick=(i)=>{
        setImage(i)
    }
    return(
        <div>
          <div className="sliderimg" >
            <Slider>
               {/* <i onClick={handleLeftClick} class="fa-solid fa-chevron-left arrowprevious"></i> */}
               <img className='slider-image slide' src={props.data[0]} alt="room"/>
               <img className='slider-image slide' src={props.data[1]} alt="room"/>
               <img className='slider-image slide' src={props.data[2]} alt="room"/>
               <img className='slider-image slide' src={props.data[3]} alt="room"/>
               {/* <i onClick={handleRightClick}  class="fa-solid fa-chevron-right arrownext"></i> */}
               </Slider>
        </div>
        </div>
        ) 
}
export default SlideShow
